DROP TABLE IF EXISTS `ratings`;CREATE TABLE `ratings` (  `key` int(10) unsigned NOT NULL,  `uid` smallint(5) unsigned NOT NULL,  `value` int(10) unsigned NOT NULL,  KEY `key` (`key`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `references`;CREATE TABLE `references` (  `id` smallint(5) unsigned NOT NULL auto_increment,  `uid` smallint(5) unsigned NOT NULL,  `who` smallint(5) unsigned NOT NULL,  `reason` text NOT NULL,  PRIMARY KEY  (`id`),  KEY `uid` (`uid`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;DROP TABLE IF EXISTS `users`;CREATE TABLE `users` (  `id` smallint(5) unsigned NOT NULL auto_increment,  `login` tinytext NOT NULL,  `pass` tinytext NOT NULL,  `state` tinyint(1) unsigned NOT NULL default '0',  `np` tinyint(3) unsigned NOT NULL default '6',  `style` tinytext NOT NULL,  `icons` tinytext NOT NULL,  `tmpl` tinytext NOT NULL,  `status` tinytext,  `name` tinytext,  `from` tinytext NOT NULL,  `sex` tinyint(3) unsigned NOT NULL default '0',  `icq` int(9) unsigned default NULL,  `jabber` tinytext NOT NULL,  `email` tinytext,  `site` tinytext,  `bday` tinyint(2) unsigned NOT NULL default '0',  `bmonth` tinyint(2) unsigned NOT NULL default '0',  `byear` smallint(4) unsigned NOT NULL default '0',  `about` text,  `posts` smallint(6) default NULL,  `avatar` tinytext,  `regdat` int(11) unsigned NOT NULL default '0',  `last` int(11) unsigned NOT NULL default '0',  `br` varchar(255) default NULL,  `ip` varchar(32) default NULL,  PRIMARY KEY  (`id`),  KEY `birthday` (`bmonth`,`bday`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8;